/** *	NavigatorInit module * *	@param selector string [message] *	@param event string [message] *	@param attribute string [message] *	@param escaped boolean [message] ***/ServiceClient.jquery.module.NavigatorInit = {	run : function(message, memory){		var result = $(message.selector);		result.live(message.event || 'click', function(){			return ServiceClient.Kernel.navigate($(this).attr(message.attribute), message.escaped || false);		});		return true;	}};