/** *	Process module * *	@param selector string *	@param navigate navigator : escaped with ; * *	@param data object *	@param run workflow object ***/ServiceClient.jquery.module.Process = {	run : function(message, memory){		if(memory.data.success){			var navigate = memory.data.navigate || message.navigate || false;			if(message.navigate || false){				navigate.replace(";", ":");			}			if(navigate){				ServiceClient.Kernel.navigate(navigate);			}			var run = memory.data.run || false;			if(run){				for(var i in run){					run[i].service = ServiceClient.Registry.get(run[i].service);				}				return ServiceClient.Kernel.run(run);			}		}		else {			$(message.selector).removeAttr('disabled');		}		memory.data = memory.data.msg;		return true;	}};