/** *	ReadForm module * *	@param selector string [message] * *	@return loadurl string *	@return request string *	@return loadparams object ***/ServiceClient.jquery.module.ReadForm = {	run : function(message, memory){		var form = $(message.selector);		memory.loadurl = form.attr('action');		memory.request = form.attr('method').toUpperCase();				var params = form.serialize();		var d= new Date();		params = params + '&_ts='+ d.getTime();		memory.loadparams = params;				return true;	}};