/** *	FormSubmit navigator * *	@element form *	@element div.status *	@element input[name=submit] * *	@param sel form-parent selector string *	@param cf boolean confirm submit [optional] false ***/ServiceClient.jquery.navigator.FormSubmit = function(config){		return [{		service : ServiceClient.jquery.view.ElementView,		elementid : config.sel +' div.status'	},{		service : ServiceClient.jquery.module.ValidateForm,		selector : config.sel + ' form'	},{		service : ServiceClient.jquery.module.ConfirmStatus,		confirm : config.cf || false,		value : 'Please confirm form submission'	},{		service : ServiceClient.jquery.module.ReadForm,		selector : config.sel + ' form'	},{		service : ServiceClient.jquery.module.ElementStatus,		selector : config.sel + ' input[name=submit]',		disabled : true	},{		service : ServiceClient.jquery.loader.AjaxLoader,		process : true,		status : 'success',		processflow : 'run',		replace : 'msg',		workflow : [{			service : ServiceClient.jquery.renderer.ContentUI		}],		errorflow : [{			service : ServiceClient.jquery.module.ElementStatus,			selector : config.sel + ' input[name=submit]'		},{			service : ServiceClient.jquery.renderer.ContentUI		}]	}];	}