/** *	SubmitLoad navigator * *	@param sel form parent selector string *	@param nav navigator : escaped with ; ***/ServiceClient.jquery.navigator.SubmitLoad = function(config){	var form = $(config.sel + ' form');		var params = form.serialize();	var d= new Date();	params._ts = d.getTime();		$(config.sel + ' input[name=submit]').attr('disabled', true);		return [{		service : ServiceClient.jquery.view.ElementView,		elementid : config.sel +' div.status'	},{		service : ServiceClient.jquery.loader.AjaxLoader,		loadurl : form.attr('action'),		loadparams : params,		workflow : [{			service : ServiceClient.jquery.module.Process,			selector : config.sel + ' input[name=submit]',			navigate : config.nav || false		},{			service : ServiceClient.jquery.renderer.ContentUI		}]	}];}