/** *	TplLoad navigator * *	@param container selector *	@param loadurl URL *	@param tpl template-index *	@param param string = escaped by ~ ***/ServiceClient.jquery.navigator.TplLoad = function(config){	if(config.param || false){		config.param = config.param.replace(/~/g, '=');	}	return [{		service : ServiceClient.jquery.view.ElementView,		elementid : config.container	},{		service : ServiceClient.jquery.loader.AjaxLoader,		loadurl : config.loadurl,		loadparams : config.param || {},		workflow : [{			service : ServiceClient.jquery.module.ApplyTemplate,			template : ServiceClient.Registry.get(config.tpl)		},{			service : ServiceClient.jquery.renderer.ContentUI		}]	}];}