/** *	TplLoad navigator * *	@param cntr selector *	@param url URL *	@param tpl template-index [optional] *	@param arg string = escaped by ~ [optional] ***/ServiceClient.jquery.navigator.TplLoad = function(config){	if(config.arg || false){		config.arg = config.arg.replace(/~/g, '=');	}	return [{		service : ServiceClient.jquery.view.ElementView,		elementid : config.cntr	},{		service : ServiceClient.jquery.loader.AjaxLoader,		loadurl : config.url,		loadparams : config.arg || {},		workflow : [{			service : ServiceClient.jquery.module.ReadTemplate,			tpl : config.tpl || false		},{			service : ServiceClient.jquery.module.ApplyTemplate		},{			service : ServiceClient.jquery.renderer.ContentUI		}]	}];}